using Hodl.Utils;

namespace Hodl.Api.UnitTest.Utils;

internal class ImageHelperTests
{
    [Test]
    [TestCase("")]
    [TestCase(null)]
    public void ImageDataFromBase64ShouldReturnNull(string value)
    {
        Assert.That(ImageHelper.ImageDataFromBase64(value), Is.Null);
    }

    [Test]
    [TestCase("ABCDEFGHIJKLMNOP")]
    [TestCase("data:image/gif;base64")]
    [TestCase("data:image/gif;base64,1234")]
    public void ImageDataFromBase64Error(string value)
    {
        Assert.Throws<InvalidOperationException>(() => ImageHelper.ImageDataFromBase64(value));
    }

    [Test]
    [TestCase("data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACwAAAAAAQABAAACAkQBADs=", 1, 1)]
    [TestCase("R0lGODlhAQABAIAAAP///wAAACwAAAAAAQABAAACAkQBADs=", 1, 1)]
    [TestCase("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAAAAAA6fptVAAAACklEQVQYV2P4DwABAQEAWk1v8QAAAABJRU5ErkJggg==", 1, 1)]
    [TestCase("iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAAAAAA6fptVAAAACklEQVQYV2P4DwABAQEAWk1v8QAAAABJRU5ErkJggg==", 1, 1)]
    [TestCase("", 220, 220)]
    [TestCase("data:image/gif;base64,R0lGODlhCAAIAHAAACwAAAAACAAIAIH///8AAAAAAAAAAAACB4SPqcvtXQAAOw==", 8, 8)]
    [TestCase("data:image/bmp;base64,Qk1aAAAAAAAAADoAAAAoAAAACAAAAAgAAAABAAQAAAAAAAAAAADEDgAAxA4AAAEAAAABAAAA/////wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA", 8, 8)]
    [TestCase("data:image/jpeg;base64,", 8, 8)]
    [TestCase("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAIAAABLbSncAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAYSURBVBhXY/z//z8DNsAEpTHAYJRgYAAAVtQDDQweRfMAAAAASUVORK5CYII=", 8, 8)]
    public void ImageDataFromBase64Test(string base64String, int width, int height)
    {
        var img = ImageHelper.ImageDataFromBase64(base64String);

        Assert.Multiple(() =>
        {
            Assert.That(img, Is.Not.Null);
            Assert.That(img.Width, Is.EqualTo(width), "Image output width is not wat was expected");
            Assert.That(img.Height, Is.EqualTo(height), "Image output height is not wat was expected");
        });
    }

